        console.log("byebye");
      }
    }, []) */

/*     function byFn() {
      console.log("bye");
    }ss
    function hiFn(){
      console.log("hi");
      return byFn;
    }
    useEffect(hiFn,[]); */

  }


  return (
    <div>
      <input value={keyword} onChange={onChange} type="text" placeholder='Searcr'/>
      <h1>{counter}</h1>
      <button onClick={onClick2}>Click me!!</button>
      <hr></hr>
      {showing ? <Hello/> : null}
      <button onClick={onShowClick}>{showing ? "Hide" : "Show"}</button>
    </div>
  );
}

export default App;
